CREATE VIEW ARTICULO AS SELECT PRODUCTO.IDPRODUCTO,PRODUCTO.NOMBREPRODUCTO,PRODUCTO.DESCRIPCION,PRODUCTO.IDCATEGORIA,PRODUCTO.IDSUBCATEGORIA,PRODUCTO.PATH,ITEM.IDSTOCK,ITEM.IDPAIS,ITEM.IDREGION,ITEM.CANTIDAD,ITEM.PRECIOVENTA FROM PRODUCTO FULL OUTER JOIN ITEM ON PRODUCTO.IDPRODUCTO=ITEM.IDPRODUCTO;
CREATE VIEW HISTORICOCOMPRA AS SELECT v.IDVENTA,v.FECHAVENTA,v.TOTAL,COUNT(dVenta.IDPRODUCTO) AS TOTALPRODUCTOS,tp.NOMBRETIPOPAGO,v.IDCLIENTE  FROM VENTA v, DETALLEVENTA dVenta,TIPOPAGO tp WHERE v.IDVENTA = dVenta.IDVENTA AND tp.IDTIPOPAGO = v.IDTIPOPAGO GROUP BY v.IDVENTA,v.FECHAVENTA,v.TOTAL,tp.NOMBRETIPOPAGO,v.IDCLIENTE;