CREATE VIEW ARTICULO AS SELECT PRODUCTO.IDPRODUCTO,PRODUCTO.NOMBREPRODUCTO,PRODUCTO.DESCRIPCION,PRODUCTO.IDCATEGORIA,PRODUCTO.IDSUBCATEGORIA,PRODUCTO.PATH,ITEM.IDPAIS,ITEM.IDREGION,ITEM.CANTIDAD,ITEM.PRECIOVENTA FROM PRODUCTO FULL OUTER JOIN ITEM ON PRODUCTO.IDPRODUCTO=ITEM.IDPRODUCTO;
--CREATE VIEW HISTORICOCOMPRA AS SELECT v.IDVENTA,v.FECHAVENTA,v.TOTAL,COUNT(dVenta.IDPRODUCTO) AS TOTALPRODUCTOS,v.IDCLIENTE  FROM VENTA v, DETALLEVENTA dVenta WHERE v.IDVENTA = dVenta.IDVENTA GROUP BY v.IDVENTA,v.FECHAVENTA,v.TOTAL,v.IDCLIENTE;